async function createAgent(baseUrl = null, apiKey = null, payload = null) {
  baseUrl = baseUrl || (typeof process !== 'undefined' && process.env && process.env.BASE_URL);
  if (!baseUrl) throw new Error('baseUrl must be provided or set BASE_URL env var');
  const headers = { 'Content-Type': 'application/json' };
  if (apiKey) headers['Authorization'] = apiKey;
  const body = JSON.stringify(payload || { agent_name: 'test-agent', agent_slug: 'test-agent', permissions: { run: true }, limits: { rpm: 10 }, description: 'Created by script' });
  const res = await fetch(`${baseUrl}/create_agent`, { method: 'POST', headers, body });
  if (!res.ok) throw new Error(`HTTP ${res.status}`);
  return await res.json();
}
createAgent().then(console.log).catch(console.error);

